import React, { useState, useEffect } from "react";
import "./App.css";
import LoginSystem from "./components/login_system/login.jsx";

function App() {
  let [status, setStatus] = useState("welcome");
  let [formContent, setFormContent] = useState("");

  useEffect(() => {
    return;
    if (status === "welcome") {
      setFormContent(
        <div className="welcome-buttons">
          <div
            className="button"
            onClick={() => setStatus("login")}
            role="button"
            href="#"
          >
            <a>Login</a>
          </div>
          <div
            className="button"
            onClick={() => setStatus("new_user")}
            role="button"
            href="#"
          >
            <a>New User</a>
          </div>
        </div>
      );
    } else {
      setFormContent(
        <LoginSystem setFormStatus={() => setStatus("welcome")} />
      );
    }
  });

  return (
    <div className="App">
      <div className="main-content">
        <h1>Hello, welcome to the game.</h1>
        {formContent}
      </div>
    </div>
  );
}

export default App;
