import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import LoginSystem from "./components/login_system/login.jsx";

function App() {
	let [status, setStatus] = useState("welcome");
	let formContent = useRef("hello world");
	let content = {
		welcome: (
			<div className="welcome-buttons">
				<div
					className="button"
					onClick={() => setStatus("login")}
					role="button"
					href="#"
				>
					<a>Login</a>
				</div>
				<div
					className="button"
					onClick={() => setStatus("new_user")}
					role="button"
					href="#"
				>
					<a>New User</a>
				</div>
			</div>
		),
		login: <LoginSystem setFormStatus={() => setStatus("welcome")} />,
	};

	useEffect(() => {});
	return (
		<div className="App">
			<h1>Hello, welcome to the game.</h1>
			<div className="main-content" ref={formContent}>
				{formContent}
			</div>
		</div>
	);
}

export default App;
