import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import LoginSystem from "./components/login_system/login.jsx";

function App() {
	let [status, setStatus] = useState("welcome");
	let formContent = useRef();

	useEffect(() => {
		if (status === "welcome") {
			formContent = (
				<div className="welcome-buttons">
					<div
						className="button"
						onClick={() => setStatus("login")}
						role="button"
						href="#"
					>
						<a>Login</a>
					</div>
					<div
						className="button"
						onClick={() => setStatus("new_user")}
						role="button"
						href="#"
					>
						<a>New User</a>
					</div>
				</div>
			);
		} else {
			formContent = (
				<LoginSystem setFormStatus={() => setStatus("welcome")} />
			);
		}
	});

	return (
		<div className="App">
			<div className="main-content">
				<h1>Hello, welcome to the game.</h1>
				{formContent}
			</div>
		</div>
	);
}

export default App;
