import React, { useEffect, useRef } from "react";
function isNameAvailable(newName) {
	console.log(newName.shadowContent);
	var names = ["sasu", "test"];
	var ret = names.find((name) => name === newName);
	if (ret) return false;
	else return true;
}
function NewUser(props) {
	const userRef = useRef(null);
	return (
		<div className="login-content">
			<h3>New User</h3>
			<form method="GET" action="" className="login-form">
				<p className="form-text">Username:</p>
				<input
					ref={userRef}
					name="user"
					type="textbox"
					onBlur={() => isNameAvailable(userRef.current)}
					placeholder="Username"
				/>
				<p className="form-text">Email:</p>
				<input
					name="user"
					type="textbox"
					placeholder="email@email.com"
				/>
				<p className="form-text">Password:</p>
				<input name="password" type="password" placeholder="Password" />
				<p className="form-text">Confirm password:</p>
				<input
					name="password-confirm"
					type="password"
					placeholder="Password"
				/>
				<input
					className="button submit-button"
					name="submit"
					type="submit"
					value="Create New User"
				/>
			</form>
			<div
				role="button"
				className="button cancel-button"
				href="#"
				onClick={props.setFormStatus}
			>
				Cancel
			</div>
		</div>
	);
}

export default NewUser;
