import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import LoginSystem from "./components/login_system/login.jsx";

function App() {
	let [status, setStatus] = useState("welcome");
	let formContent = useRef(null);

	if (status === "welcome") {
		formContent.current.innerhtml;
		formContent.current = (
			<div className="welcome-buttons">
				<div
					className="button"
					onClick={() => setStatus("login")}
					role="button"
					href="#"
				>
					<a>Login</a>
				</div>
				<div
					className="button"
					onClick={() => setStatus("new_user")}
					role="button"
					href="#"
				>
					<a>New User</a>
				</div>
			</div>
		);
	} else {
		formContent.current = (
			<LoginSystem setFormStatus={() => setStatus("welcome")} />
		);
	}

	return (
		<div className="App">
			<h1>Hello, welcome to the game.</h1>
			<div className="main-content">
				<div ref={formContent}></div>
			</div>
		</div>
	);
}

export default App;
