import './App.css';
import LoginSystem from "./components/login_system/login.jsx"

function App() {
  let [status, setStatus] = useState("welcome");
  let [formContent, setFormContent] = useState("");

  useEffect(()=>{
    if (status === "welcome")
    {
      setFormContent(
          <div className="welcome-buttons">
            <div className="button" onClick={()=>{setStatus("login")}} role="button" href="#"><a>Login</a></div>
            <div className="button" onClick={()=>{setStatus("new_user")}}role="button" href="#"><a>New User</a></div>
          </div>);
    }

      }
  );
  return (
    <div className="App">
      <h1>Hello, welcome to the game.</h1>
      {formContent}
    </div>
  );
}

export default App;
